import Log from './hls_plugin_log.js'
import Share from './hls_plugin_share.js'
import Account from './hls_plugin_account.js'
import AudioPlay from './hls_plugin_audio_play.js'
import AudioRecord from './hls_plugin_audio_record.js'
import { getLoginUserInfo, getShareAppInstallStatus, removeLoginUserInfo } from './hls_app_env_storage.js'
import { startAppEventDispatch } from './hls_app_event_dipatch.js'
import APP_EVENT from './hls_app_event.js'
import { callNative } from './hls_plugin_base.js'

/**
 * 
 */
class HlsSDK {
	constructor(arg) {
		
	}
}

//应用事件对象
HlsSDK.APP_EVENT = APP_EVENT;

//日志
HlsSDK.Log = new Log();

//分享
HlsSDK.Share = new Share();

//账号
HlsSDK.Account = new Account();

//音频播放
HlsSDK.AudioPlay = new AudioPlay();

//录音
HlsSDK.AudioRecord = new AudioRecord();

/**
 * 通过模块化方式调用原生方法
 */
HlsSDK.callNative = callNative;

/**
 * 开启应用事件监听
 */
HlsSDK.startAppEventDispatch = startAppEventDispatch;

/**
 * 从持久化获取登录用户基础信息
 */
HlsSDK.getLoginUserInfo = getLoginUserInfo;

/**
 * 从持久化获取第三方App(微信、微博、QQ)安装状态
 */
HlsSDK.getShareAppInstallStatus = getShareAppInstallStatus;

/**
 * 删除登录用户基础信息
 */
HlsSDK.removeLoginUserInfo = removeLoginUserInfo;

export default HlsSDK;