
export const PREFIX = "hls_"

export const LOGIN = "hls_login"
	
export const LOGOUT = "hls_logout"

/**
 * APP的一些自定义事件
 */
class APP_EVENT {
	
}

/**
 * 自定义事件的前缀，为了防止在
 * 应用内流转的时候与别的事件名冲突
 */
APP_EVENT.PREFIX = "hls_"

/**
 * 用户登录成功事件
 */
APP_EVENT.LOGIN = "hls_login"

/**
 * 用户登出事件
 */
APP_EVENT.LOGOUT = "hls_logout"

/**
 * 运行时资源已更新
 */
APP_EVENT.RUNTIME_UPDATE = "hls_runtimeUpdate"

/**
 * 小程序已经下架
 */
APP_EVENT.APPLET_OFFLINE = "hls_appletOffline"

export default APP_EVENT;