import { callNative } from './hls_plugin_base.js'

const LOG_MODULE = "log"

const LOG_LEVEL_INFO = "info"

const LOG_LEVEL_DEBUG = "debug"

const LOG_LEVEL_WARN = "warn"

const LOG_LEVEL_ERROR = "error"

/**
 * 日志组件
 */
class Log {
		
	/**
	 * 让客户端的日志模块打印Info级别的日志
	 * @param {Object} content
	 */
	writeInfoLog(content)
	{
		this.writeLog(content, LOG_LEVEL_INFO)
	}
	
	/**
	 * 让客户端的日志模块打印Debug级别的日志
	 * @param {Object} content
	 */
	writeDebugLog(content) {
		this.writeLog(content, LOG_LEVEL_DEBUG)
	}
	
	/**
	 * 让客户端打印指定级别的日志
	 * @param {Object} content
	 * @param {Object} level
	 */
	writeLog(content, level) {
		
		let params = {
			'content': content
		}
		
		if(level == null || level == undefined) {
			params['level'] = 'info'
		} else {
			params['level'] = level
		}
		
		return callNative(LOG_MODULE, "writeLog", params, "打印小程序日志")
	}
}

export default Log;
